SELECT Plan_ID, tbl_Plan.Member_ID, Member_Name, Member_Picture, Plan_Title, Plan_StartDate, Plan_EndDate, Plan_EndChk, Plan_Like_Cnt,tbl_Plan.Created, tbl_Plan.Updated FROM tbl_Plan JOIN tbl_Member ON tbl_Plan.Member_ID=tbl_Member.Member_ID WHERE tbl_Plan.Updated > :sql_last_value

SELECT Plan_ID, Plan_Detail.Plan_Detail_ID, Contents_ID, Category_Value_kor, Contents_Title, Contents_Keyword, IFNULL(Memo_Contents,'') AS Memo_Contents, Plan_Detail_StartTime, Plan_Detail_EndTime, Plan_Detail_Date, Plan_Detail.Created, Plan_Detail.Updated FROM (SELECT Plan_ID, Plan_Detail_ID, tbl_Plan_Detail.Contents_ID, Category_Value_kor, Contents_Title, Contents_Keyword, IFNULL(Plan_Detail_StartTime,'') AS Plan_Detail_StartTime, IFNULL(Plan_Detail_EndTime,'') AS Plan_Detail_EndTime, IFNULL(Plan_Detail_Date,'') AS Plan_Detail_Date, tbl_Plan_Detail.Created, tbl_Plan_Detail.Updated FROM tbl_Plan_Detail JOIN tbl_Contents ON tbl_Plan_Detail.Contents_ID = tbl_Contents.Contents_ID) AS Plan_Detail LEFT JOIN tbl_Memo ON Plan_Detail.Plan_Detail_ID = tbl_Memo.Plan_Detail_ID WHERE Plan_Detail.Updated > :sql_last_value


